import React, {useState} from 'react';
import './App.css';
import {Input,Button} from 'antd';
import {Link} from 'react-router-dom'

function ScreenHome() {

  const [signUpUsername, setSignUpUsername] = useState('')
  const [signUpEmail, setSignUpEmail] = useState('')
  const [signUpPassword, setSignUpPassword] = useState('')

  const handleSubmitSignup = async () => {
    const data = await fetch(('sign-up', {
        method : 'POST',
        headers: {'Content-Type' : 'application/x-www-form-urlencoded'},
        body:`usernameFromFront=${signUpUsername}&emailFromFront=${signUpEmail}&passwordFromFront=${signUpPassword}`

    }))


  }
  return (
    <div className="Login-page" >

          {/* SIGN-IN */}

          <div className="Sign">

                  <Input onChange={(e) => setSignUpUsername(e.target.value)} className="Login-input" placeholder="username" />

                  <Input onChange={(e) => setSignUpEmail(e.target.value)} className="Login-input" placeholder="email" />

                  <Input.Password onChange={(e) => setSignUpPassword(e.target.value)}  className="Login-input" placeholder="password" />


       <Button onClick={handleSubmitSignup} style={{width:'80px'}} type="primary">Sign-in</Button>

          </div>

          {/* SIGN-UP */}

          <div className="Sign">

                  <Input className="Login-input" placeholder="Arthur G" />

                  <Input.Password className="Login-input" placeholder="password" />


                  <Link to="/screensource"><Button href="" style={{width:'80px'}} type="primary">Sign-up</Button></Link>

          </div>

      </div>
  );
}

export default ScreenHome;
